import { Component } from '@angular/core';
import { IconProp } from '@fortawesome/fontawesome-svg-core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})

export class AppComponent {
  title = 'courses-app';
  username = "Harry Potter";
  logoutLoginBtn = this.username ? "Logout" : "Login";

  infoTitle = "Your List is empty";
  infoText = "Please use 'Add New Course' button to add your first course";
  addNewBtn = "Add new course";

  editableVal = true;
  showCourseBtn = "Show course";
  editBtn : IconProp = ['fas', 'pen'];
  removeBtn : IconProp = ['fas', 'trash'];
  courseTitle = "Angular";
  courseDesc = "Angular is a popular open-source web application framework developed by Google. It is used for building single-page applications (SPAs), which are web applications or websites that interact with the user by dynamically rewriting the current web page with new data from the web server, instead of loading entire new pages. Angular uses TypeScript, a statically typed superset of JavaScript, which introduces powerful features like type checking, object-oriented programming principles, and decorators.";
  courseCreation = new Date('2022-11-15');
  courseDuration = 2.5;
  courseAuthors = ["Dave Haisenberg", "Tony Ja"];

  displayCourse($event) {
    if (event)
      console.log("hi!");
  };
}
<app-header>
  <div>
    <span style="display: inline-block; margin-right: 5pt;">
      <p>{{ username }}</p>
    </span>
    <span style="display: inline-block;">
      <app-button [buttonText]="logoutLoginBtn"></app-button>
    </span>
  </div>
</app-header>
<div class="app-root">
  <!-- Search component -->
  <!--<app-search></app-search>-->

  <!-- Info component -->
  <app-info [title]="infoTitle" [text]="infoText">
    <app-button [buttonText]="addNewBtn"></app-button>
  </app-info>

  <!-- Course Card component -->
  <app-course-card [editable]="editableVal" [title]="courseTitle" [description]="courseDesc" 
    [creationDate]="courseCreation" [duration]="courseDuration" [authors]="courseAuthors"
    (clickOnShow)="displayCourse($event)">
    <app-button [iconName]="editBtn" [hidden]=!editableVal></app-button>
    <app-button [iconName]="removeBtn" [hidden]=!editableVal></app-button>
    
  </app-course-card>

  <!-- Login Form component -->
  <!--<app-login-form></app-login-form>-->

  <!-- Login Form component -->
  <!--<app-registration-form></app-registration-form>-->

  <!-- Login Form component -->
  <!--<app-course-form></app-course-form>-->

  <!-- Modal component -->
  <!-- <app-modal [message]="'Message'" [title]="'Title'" [okButtonText]="'Ok'" [cancelButtonText]="'Cancel'"></app-modal> -->
</div>
