import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-course-card',
  templateUrl: './course-card.component.html',
  styleUrls: ['./course-card.component.scss']
})
export class CourseCardComponent {

  @Input() editable = true;
  @Input() title = "";
  @Input() description = "";
  @Input() creationDate = "";
  @Input() duration = 0;
  @Input() authors:string[] = [];
  editBtn : IconProp = ['fas', 'pen'];
  removeBtn : IconProp = ['fas', 'trash'];
}
<div class="course-card">
    <div class="course-card__description">
      <!-- Replace ::text:: with your code -->
      <div class="course-card__description-title">{{ title }}</div>
      <div class="course-card__description-text">{{ description }}</div>
    </div>
    <div class="course-card__info">
        <div class="course-card__info-text">
            <div>
              <b>Authors: </b>
              {{ authors }}
            </div>
            <div><b>Duration: </b><span class="course-card__duration">{{ duration }}</span></div>
            <div><b>Created: </b><span class="course-card__date">{{ creationDate }}</span></div>
        </div>
        <div class="course-card__info-actions">
<!--            <app-button></app-button>-->
            <div class="course-card__buttons">
                <!--Enter your code here-->
                <div>
                    <span style="display: inline-block; margin-right: 5pt;">
                      <app-button [buttonText]="showCourseBtn"></app-button>
                    </span>
                    <span style="display: inline-block; margin-right: 5pt;">
                      <app-button [iconName]="editBtn"></app-button>
                    </span>
                    <span style="display: inline-block;">
                      <app-button [iconName]="removeBtn"></app-button>
                    </span>
                  </div>
              
            </div>
        </div>
    </div>
</div>
